@import url('https://fonts.googleapis.com/css2?family=Nunito:wght@200;300;400;600&display=swap');

*{
  font-family: 'Poppins', sans-serif;
  margin:0; padding:0;
  box-sizing: border-box;
  text-decoration: none;
  outline: none; border:none;
  text-transform: capitalize;
  transition: all .2s linear;
}

$red:#a4af0ed2;
$black : #000;
$white : #fff;
$light-white:#aaa;
$light-bg:#111;

@mixin grid($val) {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax($val, 1fr));
  gap:1.5rem;
}

html{
  font-size: 62.5%;
  scroll-behavior: smooth;
  scroll-padding-top: 5rem;
  overflow-x: hidden;

  &::-webkit-scrollbar{
    width: 1rem;
  }

  &::-webkit-scrollbar-track{
    background: $black;
  }

  &::-webkit-scrollbar-thumb{
    background: $red;
    border-radius: 3rem;
  }
}

body{
  background: $black;
}

section{
  padding:5rem 9%;
}

.heading{
  text-align: center;
  margin-bottom: 3rem;
  position: relative;

  &::before{
    content: '';
    position: absolute;
    top:50%; left:0;
    transform: translateY(-50%);
    height: .1rem;
    width: 100%;
    background: $light-white;
    z-index: -1;
  }

  span{
    font-size: 3rem;
    color:$red;
    background: $black;
    padding:.5rem 1.5rem;
    border:.1rem solid $light-white;
    border-radius: .5rem;
  }
}

.btn{
  margin-top: 1rem;
  display: inline-block;
  padding:1rem 2.8rem;
  padding-right: 3rem;
  background:linear-gradient(130deg, $red 93%, transparent 90%);
  color:$white;
  cursor: pointer;
  font-size: 1.7rem;

  &:hover{
    transform:scale(1.1);
  }
}

.header{
  position: fixed;
  top:0; left:0; right:0;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding:0 9%;
  z-index: 10000;
  background: $black;

  .logo{
    font-weight: bolder;
    color:$white;
    font-size: 3rem;

    span{
      color:$red;
    }
  }

  .navbar a{
    display: inline-block;
    padding:1.5rem 2rem;
    font-size: 1.7rem;
    color:$white;

    &:hover{
      background: $red;
    }
  }
}

#menu-btn{
  font-size: 3rem;
  color:$white;
  cursor: pointer;
  display: none;
}

.home{
  padding:0;
  margin-top: 5rem;

  .slide{
    min-height:70vh;
    background-size: cover !important;
    background-position: center !important;
    padding:2rem 9%;
    display: flex;
    align-items: center;

    .content{
      width:60rem;

      span{
        font-size: 2rem;
        color:$red;
        display: block;
      }

      h3{
        font-size: 5rem;
        color:$white;
        padding:1rem 0;
      }
    }
  }
}

.swiper-pagination-bullet{
  height: 2rem;
  width: 2rem;
  background: $white;
  border-radius: 0;

  &.swiper-pagination-bullet-active{
    background: $red;
  }
} 

.about{
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  gap:8rem;

  .image{
    flex:1 1 42rem;
    position: relative;

    &::before,
    &::after{
      content: '';
      position: absolute;
      z-index: -1;
      background: $red;
      height: 15rem;
      width: 15rem;
    }

    &::before{
      top:0; left:0;
    }

    &::after{
      bottom:0; right:0;
    }

    img{
      width: 100%;
      padding:2rem;
    }
  }

  .content{
    flex: 1 1 42rem;

    span{
      font-size: 2rem;
      color:$red;
    }

    .title{
      font-size: 4rem;
      color:$white;
      margin-top: .5rem;
    }

    p{
      font-size: 1.5rem;
      color:$light-white;
      padding: 1rem 0;
      line-height: 2;
    }

    .box-container{
      display: flex;
      flex-wrap: wrap;
      gap:2rem;
      margin-top: 1rem;

      .box{
        flex:1 1 20rem;

        h3{
          font-size: 2rem;
          color:$white;

          i{
            padding-right: 1.5rem;
            color:$red;
          }
        }
      }
    }
  }
}

.features .box-container{
  @include grid(32rem);
  gap:0;

  .box{
    background: $light-bg;

    &.second{
      display: flex;
      flex-flow: column-reverse;
    }

    &:hover .image img{
      transform: scale(1.1);
    }

    .image{
      height: 30rem;
      width: 100%;
      overflow:hidden;

      img{
        height: 100%;
        width: 100%;
        object-fit: cover;
      }
    }

    .content{
      padding:2rem;
      text-align: center;

      img{
        height: 9rem;
        margin-bottom: 1rem;
        filter: invert(1);
      }

      h3{
        font-size: 2rem;
        color:$red;
      }

      p{
